0,'what_to_show','Y',1,'posts',20,8,'',1,'yes'),(32,0,'date_format','Y',1,'F j, Y',20,8,'',1,'yes'),(33,0,'time_format','Y',1,'g:i a',20,8,'',1,'yes'),(34,0,'links_updated_date_format','Y',1,'F j, Y g:i a',20,8,'',1,'yes'),(35,0,'links_recently_updated_prepend','Y',1,'<em>',20,8,'',1,'yes'),(36,0,'links_recently_updated_append','Y',1,'</em>',20,8,'',1,'yes'),(37,0,'links_recently_updated_time','Y',1,'120',20,8,'',1,'yes'),(38,0,'comment_moderation','Y',1,'',20,8,'',1,'yes'),(39,0,'moderation_notify','Y',1,'1',20,8,'',1,'yes'),(40,0,'permalink_structure','Y',1,'',20,8,'',1,'yes'),(41,0,'gzipcompression','Y',1,'0',20,8,'',1,'yes'),(42,0,'hack_file','Y',1,'0',20,8,'',1,'yes'),(43,0,'blog_charset','Y',1,'UTF-8',20,8,'',1,'yes'),(44,0,'moderation_keys','Y',1,'-online\r\n4u\r\nadipex\r\nadvicer\r\nambien\r\nbaccarrat\r\nblackjack\r\nbllogspot\r\nbooker\r\nbyob\r\ncar-rental-e-site\r\ncar-rentals-e-site\r\ncarisoprodol\r\ncasino\r\ncasinos\r\nchatroom\r\ncialis\r\ncredit-report-4u\r\ncwas\r\ncyclen\r\ncyclobenzaprine\r\ndating-e-site\r\nday-trading\r\ndebt-consolidation-consultant\r\ndrug\r\ndiscreetordering\r\nduty-free\r\ndutyfree\r\nequityloans\r\nfioricet\r\nflowers-leading-site\r\nfreenet-shopping\r\nfreenet\r\ngambling\r\nhealth-insurancedeals-4u\r\nhomeequityloans\r\nhomefinance\r\nholdem\r\nholdempoker\r\nholdemsoftware\r\nholdemtexasturbowilson\r\nhotel-dealse-site\r\nhotele-site\r\nhotelse-site\r\nincest\r\ninsurance-quotesdeals-4u\r\ninsurancedeals-4u\r\njrcreations\r\nlevitra\r\nloan\r\nmacinstruct\r\nmortgage-4-u\r\nmortgagequotes\r\nonline-gambling\r\nonlinegambling-4u\r\nottawavalleyag\r\nownsthis\r\npalm-texas-holdem-game\r\npaxil\r\npenis\r\npharmacy\r\nphentermine\r\npoker\r\npoker-chip\r\npoze\r\nrental-car-e-site\r\nroulette \r\nshemale\r\nshoes\r\nslot-machine\r\nslot\r\nsoma\r\ntaboo\r\nteen\r\ntexas-holdem\r\nthorcarlson\r\ntop-site\r\ntop-e-site\r\ntramadol\r\ntrim-spa\r\nultram\r\nvaleofglamorganconservatives\r\nviagra\r\nvioxx\r\nxanax\r\nzolus',2